package com.example.nequiapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import android.os.Bundle;

import java.util.ArrayList;

public class ListAccountActivity extends AppCompatActivity {

    private ArrayList<Account> listAccountRV=new ArrayList<>();
    private RecyclerView rvListAccount;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        setContentView(R.layout.activity_list_account);
        loadFakeData();
        rvListAccount=findViewById(R.id.rv_account);
        AccountAdapter myAdapter=new AccountAdapter(listAccountRV);
        rvListAccount.setAdapter(myAdapter);
        rvListAccount.setLayoutManager(new LinearLayoutManager(ListAccountActivity.this));
    }

    private void loadFakeData(){
        Account myAccount1= new Account("Bancolombia","Cuenta Corriente",1000012.0,"data:image/png;base64,");
        Account myAccount2= new Account("Davivienda","Cuenta Corriente",704512.0,"https://s3.amazonaws.com/lacabinadavivienda.com/projects/assets_generales/img/logo-davivienda.png?v=2");
        Account myAccount3= new Account("JPMorgan","Credit card",12.0,"https://upload.wikimedia.org/wikipedia/commons/thumb/a/af/J_P_Morgan_Logo_2008_1.svg/2560px-J_P_Morgan_Logo_2008_1.svg.png");
        listAccountRV.add(myAccount1);
        listAccountRV.add(myAccount2);
        listAccountRV.add(myAccount3);
    }

}